import java.util.Scanner;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner sc = new Scanner(System.in);
		while(true){
			int numA = sc.nextInt();
			int numB = sc.nextInt();
			System.out.println(numA+numB);
			break;
		}
	}
}